import { connect } from "react-redux";
import {
  addToCart,
  clearCart,
  decreaseCart,
  getTotals,
  removeFromCart,
} from "../slices/cartSlice";
import { Link } from "react-router-dom";

class Cart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      age: "",
      address: "",
      phoneNumber: "",
    };
  }

  componentDidMount() {
    this.props.dispatch(getTotals());
  }

  handleAddToCart = (product) => {
    this.props.dispatch(addToCart(product));
  };

  handleDecreaseCart = (product) => {
    this.props.dispatch(decreaseCart(product));
  };

  handleRemoveFromCart = (product) => {
    this.props.dispatch(removeFromCart(product));
  };

  handleClearCart = () => {
    this.props.dispatch(clearCart());
  };

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    // Perform form submission and validation here
    const {
      firstName,
      lastName,
      age,
      address,
      phoneNumber,
    } = this.state;

    // Perform basic form validation
    if (!firstName || !lastName || !age || !address || !phoneNumber) {
      alert("Please fill out all fields.");
      return;
    }

    // Perform additional validation if needed
    if (isNaN(age) || isNaN(phoneNumber)) {
      alert("Invalid age or phone number.");
      return;
    }

    // If all validations pass, you can proceed with the submission
    const formData = {
      firstName,
      lastName,
      age: parseInt(age),
      address,
      phoneNumber,
      cartItems: this.props.cart.cartItems,
      totalAmount: this.props.cart.cartTotalAmount,
    };

    // Perform your submission logic here, such as sending data to a server
    // For demonstration, let's just log the form data
    console.log("Form data:", formData);

    // Clear the form fields after successful submission
    this.setState({
      firstName: "",
      lastName: "",
      age: "",
      address: "",
      phoneNumber: "",
    });

    // Optionally, you can navigate to a confirmation page or perform other actions
  };

  render() {
    const { cart } = this.props;
    const {
      firstName,
      lastName,
      age,
      address,
      phoneNumber,
    } = this.state;

    return (
      <div className="cart-container">
        <h2>Shopping Cart</h2>
        {cart.cartItems.length === 0 ? (
          <div className="cart-empty">
            <p>Your cart is currently empty</p>
            <div className="start-shopping">
              <Link to="/">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="20"
                  height="20"
                  fill="currentColor"
                  className="bi bi-arrow-left"
                  viewBox="0 0 16 16"
                >
                  {/* ... SVG path data ... */}
                </svg>
                <span>Start Shopping</span>
              </Link>
            </div>
          </div>
        ) : (
          <div>
            <div className="titles">
              {/* ... Existing titles ... */}
            </div>
            <div className="cart-items">
              {/* ... Existing cart items rendering ... */}
            </div>
            <div className="cart-summary">
              {/* ... Existing summary rendering ... */}
            </div>
            <div className="checkout-form">
              <h3>Checkout Form</h3>
              <form onSubmit={this.handleSubmit}>
                <div className="form-group">
                  <label htmlFor="firstName">First Name:</label>
                  <input
                    type="text"
                    name="firstName"
                    value={firstName}
                    onChange={this.handleInputChange}
                    required
